// Fixed Dashboard Data Processing
const allData = $input.all();
// Safely log the structure
const firstItem = allData[0]?.json;
if (Array.isArray(firstItem)) {
  console.log('Raw input structure (first 2 items):', JSON.stringify(firstItem.slice(0, 2), null, 2));
} else {
  console.log('Raw input structure:', JSON.stringify(firstItem, null, 2));
}

// The input is coming as a single array of mixed records
// We need to handle this flat structure
let allRecords = [];

// Extract all records from the input
allData.forEach(item => {
  if (item.json) {
    if (Array.isArray(item.json)) {
      allRecords = allRecords.concat(item.json);
    } else {
      allRecords.push(item.json);
    }
  }
});

console.log(`Total records to process: ${allRecords.length}`);

// Initialize data containers
let opportunities = [];
let mlThreshold = null;
let dailyPerformance = [];
let exchangePerformance = [];
let xrplOpportunities = [];
let gasOptimization = null;
let riskStatus = null;

// Process each record and identify its type
allRecords.forEach(record => {
  // Real-Time Monitor records (opportunities)
  if (record.execution_status !== undefined && record.pair !== undefined && record.profit_potential !== undefined) {
    opportunities.push(record);
    console.log(`Found opportunity: ${record.pair} - Status: ${record.execution_status}, Profit: ${record.profit_potential}%`);
  }
  // ML Threshold record
  else if (record.optimal_threshold !== undefined && record.confidence !== undefined) {
    mlThreshold = record;
    console.log('Found ML threshold data');
  }
  // Daily Performance records
  else if (record.total_opportunites !== undefined && record.date !== undefined) {
    dailyPerformance.push(record);
    console.log(`Found daily performance: ${record.date}`);
  }
  // Exchange Performance records
  else if (record.exchange_name !== undefined && record.avg_spread_percentage !== undefined) {
    exchangePerformance.push(record);
    console.log(`Found exchange performance: ${record.exchange_name}`);
  }
  // XRPL Opportunities
  else if (record.xrpl_xrp_price !== undefined && record.eth_xrp_price !== undefined) {
    xrplOpportunities.push(record);
    console.log('Found XRPL opportunity');
  }
  // Gas Optimization
  else if (record.current_gas_gwei !== undefined && record.execution_window !== undefined && record.recommended_threshold !== undefined) {
    gasOptimization = record;
    console.log('Found gas optimization data');
  }
  // Risk Management
  else if (record.risk_status !== undefined && record.total_portfolio_value !== undefined) {
    riskStatus = record;
    console.log('Found risk status data');
  }
});

console.log(`\nData Summary:
- Opportunities: ${opportunities.length}
- ML Threshold: ${mlThreshold ? 'Yes' : 'No'}
- Daily Performance: ${dailyPerformance.length}
- Exchange Performance: ${exchangePerformance.length}
- XRPL Opportunities: ${xrplOpportunities.length}
- Gas Optimization: ${gasOptimization ? 'Yes' : 'No'}
- Risk Status: ${riskStatus ? 'Yes' : 'No'}`);

// Calculate 24h metrics
const now = new Date();
const cutoff24h = new Date(now.getTime() - 24 * 60 * 60 * 1000);

const last24h = opportunities.filter(opp => {
  if (!opp.timestamp) return false;
  const oppTime = new Date(opp.timestamp);
  return oppTime > cutoff24h;
});

console.log(`Opportunities in last 24h: ${last24h.length}`);

const executed24h = last24h.filter(o => o.execution_status === 'Executed');
const totalProfit24h = executed24h.reduce((sum, o) => {
  const profit = parseFloat(o.realized_profit || o.net_profit_estimate || 0);
  // Filter out unrealistic values
  if (Math.abs(profit) > 100000) return sum;
  return sum + profit;
}, 0);

const winningTrades = executed24h.filter(o => {
  const profit = parseFloat(o.realized_profit || o.net_profit_estimate || 0);
  return profit > 0;
});

const winRate24h = executed24h.length > 0 
  ? (winningTrades.length / executed24h.length) * 100
  : 0;

console.log(`Executed trades: ${executed24h.length}, Winning: ${winningTrades.length}, Win rate: ${winRate24h}%`);

// ML performance metrics
const mlGuidedTrades = opportunities.filter(o => 
  o.execution_status === 'Executed' && 
  o.dynamic_threshold !== undefined
);
const mlSuccess = mlGuidedTrades.filter(o => parseFloat(o.realized_profit || o.net_profit_estimate || 0) > 0).length;
const mlWinRate = mlGuidedTrades.length > 0 ? (mlSuccess / mlGuidedTrades.length) * 100 : 0;

// Top exchanges
const topExchanges = exchangePerformance
  .sort((a, b) => (b.execution_success_rate || 0) - (a.execution_success_rate || 0))
  .slice(0, 5);

// XRPL metrics
const xrplProfitable = xrplOpportunities.filter(o => o.would_execute === true || o.would_execute === "true").length;
const xrplTotal = xrplOpportunities.filter(o => o.net_profit_after_bridge !== undefined).length;
const xrplOpportunityRate = xrplTotal > 0 ? (xrplProfitable / xrplTotal) * 100 : 0;

// 7-day trend
const profitTrend = dailyPerformance.length >= 2 
  ? (dailyPerformance[0].total_profit_usd || 0) - (dailyPerformance[dailyPerformance.length - 1].total_profit_usd || 0)
  : 0;

const avgDailyProfit = dailyPerformance.length > 0
  ? dailyPerformance.reduce((sum, d) => sum + (d.total_profit_usd || 0), 0) / dailyPerformance.length
  : 0;

// ML threshold history
const thresholdHistory = mlThreshold ? [
  { time: '1hr ago', value: (mlThreshold.optimal_threshold * 0.95).toFixed(2) },
  { time: '30min ago', value: (mlThreshold.optimal_threshold * 0.98).toFixed(2) },
  { time: 'Now', value: (mlThreshold.optimal_threshold || 0.5).toFixed(2) }
] : [];

// Prepare dashboard data
const dashboardData = {
  // Core metrics
  metrics: {
    profit24h: totalProfit24h.toFixed(2),
    winRate24h: winRate24h.toFixed(1),
    totalOpportunities24h: last24h.length,
    executedTrades24h: executed24h.length,
    avgDailyProfit: avgDailyProfit.toFixed(2),
    profitTrend: profitTrend >= 0 ? 'positive' : 'negative',
    profitTrendValue: Math.abs(profitTrend).toFixed(2),
    mlWinRate: mlWinRate.toFixed(1),
    mlGuidedTradesCount: mlGuidedTrades.length,
    xrplOpportunityRate: xrplOpportunityRate.toFixed(1),
    xrplProfitableCount: xrplProfitable
  },

  // ML data
  mlData: {
    currentThreshold: mlThreshold?.optimal_threshold?.toFixed(2) || '0.50',
    confidence: mlThreshold ? (parseFloat(mlThreshold.confidence) * 100).toFixed(1) : '0',
    f1Score: mlThreshold && mlThreshold.f1_score ? (parseFloat(mlThreshold.f1_score) * 100).toFixed(1) : '0',
    trainingSamples: mlThreshold?.training_size || 0,
    thresholdHistory: thresholdHistory,
    lastUpdated: mlThreshold?.timestamp || 'N/A'
  },

  // Gas optimization
  gasData: {
    currentGas: gasOptimization?.current_gas_gwei || 0,
    executionWindow: gasOptimization?.execution_window || 'Unknown',
    recommendedThreshold: gasOptimization?.recommended_threshold || 0.5,
    missedOpportunities: gasOptimization?.missed_opportunities_count || 0,
    potentialLost: gasOptimization?.potential_profit_lost || 0,
    nextOptimalWindow: gasOptimization?.next_optimal_window || 'Unknown'
  },

  // Risk management
  riskData: {
    status: riskStatus?.risk_status || 'Safe',
    portfolioValue: riskStatus?.total_portfolio_value || 50000,
    dailyPnl: riskStatus?.daily_pnl || 0,
    activePositions: riskStatus?.active_positions_count || 0,
    maxDrawdown: riskStatus?.max_drawdown_percent || 0,
    haltTrading: riskStatus?.halt_trading || false,
    riskAdjustedTradeSize: riskStatus?.risk_adjusted_trade_size || 1000
  },

  // Exchange rankings
  topExchanges: topExchanges.map(ex => ({
    name: ex.exchange_name,
    successRate: ex.execution_success_rate || 0,
    avgSpread: ex.avg_spread_percentage || 0,
    liquidityScore: ex.avg_liquidity_score || 0,
    reliability: ex.reliability_rating || 'Unknown',
    bestPair: ex.best_trading_pairs || 'N/A'
  })),

  // Recent activity - show all opportunities, not just last 24h for demo
  recentActivity: opportunities
    .filter(opp => opp.pair && opp.execution_status) // Only include valid records
    .slice(0, 10)
    .map(opp => ({
      timestamp: opp.timestamp ? new Date(opp.timestamp).toLocaleTimeString() : 'Unknown',
      pair: opp.pair,
      status: opp.execution_status,
      profit: parseFloat(opp.realized_profit || opp.net_profit_estimate || 0).toFixed(2),
      profitPercent: parseFloat(opp.profit_potential || 0).toFixed(1),
      reason: opp.skip_reason || 'Executed',
      threshold: opp.dynamic_threshold || 0,
      window: opp.execution_window || 'Unknown'
    })),

  // Daily performance chart
  dailyChart: dailyPerformance
    .filter(d => d.date) // Only include valid records
    .map(d => ({
      date: new Date(d.date).toLocaleDateString('en', { weekday: 'short', month: 'short', day: 'numeric' }),
      profit: d.total_profit_usd || 0,
      opportunities: d.total_opportunites || 0,
      winRate: d.success_rate || 0
    }))
    .reverse(),

  // XRPL data
  xrplData: xrplOpportunities
    .filter(x => x.timestamp && x.spread_percent !== undefined) // Only include valid records
    .slice(0, 5)
    .map(x => ({
      timestamp: new Date(x.timestamp).toLocaleTimeString(),
      spread: x.spread_percent || 0,
      profit: x.net_profit_after_bridge || 0,
      quality: x.opportunity_quality || 'Unknown',
      wouldExecute: x.would_execute || false
    })),

  // System status
  systemStatus: {
    botActive: true,
    lastUpdate: new Date().toLocaleTimeString(),
    dataFreshness: 'Real-time',
    nextRefresh: '30 seconds'
  }
};

console.log('\n=== Final Dashboard Data ===');
console.log(`24H Metrics: ${executed24h.length} executed, $${totalProfit24h.toFixed(2)} profit`);
console.log(`Recent Activity: ${dashboardData.recentActivity.length} items`);
console.log(`Top Exchanges: ${dashboardData.topExchanges.length} exchanges`);
console.log(`XRPL Data: ${dashboardData.xrplData.length} opportunities`);

return [{ json: dashboardData }];

